mapper.select:
select t1.* , p.`name` as proName,p.username as proUserName ,p.usertel as proUserTel,p.useropenid as proUserOpenid from v_vote t1 , ( SELECT p.activity_code, p.vote_code, p.`name`, u. NAME AS username, u.tel AS usertel, u.openid as useropenid FROM v_production p, v_activity_user u WHERE p.user_id = u.id ) p , v_activity a where 1=1 and t1.activity_code = p.activity_code AND t1.vote_code = p.vote_code and t1.activity_code = a.code
and p.name like CONCAT('%', #{proName},'%')
and a.name like CONCAT('%', #{actName},'%')
and t1.vote_code = #{vote_code}
and t1.vote_date BETWEEN #{vote_start_date} and #{vote_end_date}
order by id desc limit #{firstrownum}, #{limit}

select count(t1.id) from v_vote t1 , ( SELECT p.activity_code, p.vote_code, p.`name`, u. NAME AS username, u.tel AS usertel FROM v_production p, v_activity_user u WHERE p.user_id = u.id ) p , v_activity a where 1=1 and t1.activity_code = p.activity_code AND t1.vote_code = p.vote_code and t1.activity_code = a.code
and p.name like CONCAT('%', #{proName},'%')
and a.name like CONCAT('%', #{actName},'%')
and t1.vote_code = #{vote_code}
and t1.vote_date BETWEEN #{vote_start_date} and #{vote_end_date}
